mapper.insert:
INSERT INTO USER_INFO (
    USER_ID
    , USER_PW
    , USER_NAME
    , USER_PHONE1
    , USER_PHONE2
    , USER_PHONE3
    , USER_ADDR1
    , USER_ADDR2
    , USER_COMPANY
    , CREATOR
    , CREATE_TIME
    , MODIFIER
    , MODIFIED_TIME
) VALUES (
    #{userId}
    , #{userPw}
    , #{userName}
    , #{userPhone1}
    , #{userPhone2}
    , #{userPhone3}
    , #{userAddr1}
    , #{userAddr2}
    , #{userCompany}
    , #{userId}
    , SYSDATE
    , #{userId}
    , SYSDATE
)

mapper.select:
SELECT COUNT(*) 
FROM USER_INFO 
WHERE USER_ID = #{userId}

SELECT *
FROM USER_INFO
WHERE USER_ID = #{userId}
AND USER_PW = #{userPw}

mapper.update:
UPDATE USER_INFO
SET deleted = 'Y'
WHERE user_id = #{userId}
